import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { TextField, Checkbox, FormControlLabel, Button, Typography, List, ListItem, ListItemSecondaryAction, IconButton } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';

const AttendanceForm = () => {
    const [studentName, setStudentName] = useState('');
    const [present, setPresent] = useState(false);
    const [date, setDate] = useState('');
    const [attendanceRecords, setAttendanceRecords] = useState([]);

    const fetchAttendanceRecords = async () => {
        const response = await axios.get('http://localhost:5000/api/attendance');
        setAttendanceRecords(response.data);
    };

    useEffect(() => {
        fetchAttendanceRecords();
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();
        await axios.post('http://localhost:5000/api/attendance', { studentName, present, date });
        fetchAttendanceRecords();
        setStudentName('');
        setPresent(false);
        setDate('');
    };

    const handleDelete = async (id) => {
        await axios.delete(`http://localhost:5000/api/attendance/${id}`);
        fetchAttendanceRecords();
    };

    return (
        <div>
            <Typography variant="h4" gutterBottom>Attendance</Typography>
            <form onSubmit={handleSubmit}>
                <TextField
                    label="Student Name"
                    variant="outlined"
                    value={studentName}
                    onChange={(e) => setStudentName(e.target.value)}
                    required
                    fullWidth
                    margin="normal"
                />
                <FormControlLabel
                    control={
                        <Checkbox
                            checked={present}
                            onChange={(e) => setPresent(e.target.checked)}
                        />
                    }
                    label="Present"
                />
                <TextField
                    label="Date"
                    type="date"
                    variant="outlined"
                    value={date}
                    onChange={(e) => setDate(e.target.value)}
                    required
                    fullWidth
                    margin="normal"
                />
                <Button type="submit" variant="contained" color="primary">Submit</Button>
            </form>
            <Typography variant="h5" gutterBottom>Attendance Records</Typography>
            <List>
                {attendanceRecords.map((record) => (
                    <ListItem key={record._id}>
                        {record.studentName} - {record.present ? 'Present' : 'Absent'} on {new Date(record.date).toLocaleDateString()}
                        <ListItemSecondaryAction>
                            <IconButton edge="end" aria-label="delete" onClick={() => handleDelete(record._id)}>
                                <DeleteIcon />
                            </IconButton>
                        </ListItemSecondaryAction>
                    </ListItem>
                ))}
            </List>
        </div>
    );
};

export default AttendanceForm;
