import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { TextField, Checkbox, FormControlLabel, Button, Typography, List, ListItem, ListItemSecondaryAction, IconButton } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';

const AttendanceForm = () => {
    const [studentName, setStudentName] = useState('');
    const [group, setGroup] = useState(''); // Новое состояние для группы
    const [present, setPresent] = useState(false);
    const [date, setDate] = useState('');
    const [attendanceRecords, setAttendanceRecords] = useState([]);
    const [isEditing, setIsEditing] = useState(false);
    const [currentId, setCurrentId] = useState(null);

    const fetchAttendanceRecords = async () => {
        const response = await axios.get('http://localhost:5000/api/attendance', {
            headers: {
                Authorization: `Bearer ${localStorage.getItem('token')}`
            }
        });
        setAttendanceRecords(response.data);
    };

    useEffect(() => {
        fetchAttendanceRecords();
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();
        const token = localStorage.getItem('token');
        if (!token) {
            alert('Пожалуйста, войдите в систему');
            return;
        }

        try {
            // Проверяем, что дата и группа не пустые
            if (!date || !group) {
                alert('Пожалуйста, введите дату и группу');
                return;
            }

            // Преобразуем дату в формат YYYY-MM-DD
            const dateParts = date.split('.');
            if (dateParts.length !== 3) {
                alert('Неверный формат даты. Используйте DD.MM.YYYY');
                return;
            }

            const formattedDate = new Date(`${dateParts[2]}-${dateParts[1]}-${dateParts[0]}`);
            if (isNaN(formattedDate)) {
                alert('Неверная дата');
                return;
            }

            if (isEditing) {
                const response = await axios.put(`http://localhost:5000/api/attendance/${currentId}`, { studentName, group, present, date: formattedDate.toISOString() }, {
                    headers: {
                        Authorization: `Bearer ${token}`
                    }
                });
                const updatedRecords = attendanceRecords.map(record => (record._id === currentId ? response.data : record));
                setAttendanceRecords(updatedRecords);
                setIsEditing(false);
            } else {
                const response = await axios.post('http://localhost:5000/api/attendance', { studentName, group, present, date: formattedDate.toISOString() }, {
                    headers: {
                        Authorization: `Bearer ${token}`
                    }
                });
                setAttendanceRecords([...attendanceRecords, response.data]);
            }

            // Сброс полей формы
            setStudentName('');
            setGroup(''); // Сброс поля группы
            setPresent(false);
            setDate('');
        } catch (error) {
            console.error('Ошибка при отправке данных:', error);
            alert('Ошибка при отправке данных');
        }
    };

    const handleEdit = (record) => {
        setStudentName(record.studentName);
        setGroup(record.group); // Устанавливаем группу для редактирования
        setPresent(record.present);
        const dateObj = new Date(record.date);
        const formattedDate = `${String(dateObj.getDate()).padStart(2, '0')}.${String(dateObj.getMonth() + 1).padStart(2, '0')}.${dateObj.getFullYear()}`;
        setDate(formattedDate);
        setIsEditing(true);
        setCurrentId(record._id);
    };

    const handleDelete = async (id) => {
        const token = localStorage.getItem('token');
        if (!token) return alert('Пожалуйста, войдите в систему');

        try {
            await axios.delete(`http://localhost:5000/api/attendance/${id}`, {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            });
            setAttendanceRecords(attendanceRecords.filter(record => record._id !== id));
        } catch (error) {
            console.error('Ошибка при удалении записи:', error);
            alert('Ошибка при удалении записи');
        }
    };

    return (
        <div>
            <Typography variant="h4">Форма посещаемости</Typography>
            <form onSubmit={handleSubmit}>
                <TextField
                    label="Имя студента"
                    value={studentName}
                    onChange={(e) => setStudentName(e.target.value)}
                    required
                />
                <TextField
                    label="Группа" // Поле для ввода группы
                    value={group}
                    onChange={(e) => setGroup(e.target.value)}
                    required
                />
                <FormControlLabel
                    control={
                        <Checkbox
                            checked={present}
                            onChange={(e) => setPresent(e.target.checked)}
                        />
                    }
                    label="Присутствует"
                />
                <TextField
                    label="Дата (DD.MM.YYYY)"
                    value={date}
                    onChange={(e) => setDate(e.target.value)}
                    required
                />
                <Button type="submit" variant="contained">
                    {isEditing ? 'Обновить' : 'Добавить'}
                </Button>
            </form>
            <List>
                {attendanceRecords.map(record => (
                    <ListItem key={record._id}>
                        <Typography>{record.studentName} - {record.group} - {record.present ? 'Присутствует' : 'Отсутствует'} - {new Date(record.date).toLocaleDateString()}</Typography>
                        <ListItemSecondaryAction>
                            <IconButton edge="end" onClick={() => handleEdit(record)}>
                                <EditIcon />
                            </IconButton>
                            <IconButton edge="end" onClick={() => handleDelete(record._id)}>
                                <DeleteIcon />
                            </IconButton>
                        </ListItemSecondaryAction>
                    </ListItem>
                ))}
            </List>
        </div>
    );
};

export default AttendanceForm;
