import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AttendanceForm = () => {
    const [studentName, setStudentName] = useState('');
    const [present, setPresent] = useState(false);
    const [attendanceRecords, setAttendanceRecords] = useState([]);

    const fetchAttendanceRecords = async () => {
        const response = await axios.get('http://localhost:3000/api/attendance');
        setAttendanceRecords(response.data);
    };

    useEffect(() => {
        fetchAttendanceRecords();
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();
        await axios.post('http://localhost:3000/api/attendance', { studentName, present });
        fetchAttendanceRecords();
        setStudentName('');
        setPresent(false);
    };

    return (
        <div>
            <h1>Attendance</h1>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    value={studentName}
                    onChange={(e) => setStudentName(e.target.value)}
                    placeholder="Student Name"
                    required
                />
                <label>
                    Present:
                    <input
                        type="checkbox"
                        checked={present}
                        onChange={(e) => setPresent(e.target.checked)}
                    />
                </label>
                <button type ="submit">Submit</button>
            </form>
            <h2>Attendance Records</h2>
            <ul>
                {attendanceRecords.map((record) => (
                    <li key={record._id}>
                        {record.studentName} - {record.present ? 'Present' : 'Absent'} on {new Date(record.date).toLocaleDateString()}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default AttendanceForm;
