import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { TextField, Checkbox, FormControlLabel, Button, Typography, List, ListItem } from '@mui/material';

const AttendanceForm = () => {
    const [studentName, setStudentName] = useState('');
    const [present, setPresent] = useState(false);
    const [attendanceRecords, setAttendanceRecords] = useState([]);

    const fetchAttendanceRecords = async () => {
        const response = await axios.get('http://localhost:5000/api/attendance');
        setAttendanceRecords(response.data);
    };

    useEffect(() => {
        fetchAttendanceRecords();
    }, []);

    const handleSubmit = async (e) => {
        e.preventDefault();
        await axios.post('http://localhost:5000/api/attendance', { studentName, present });
        fetchAttendanceRecords();
        setStudentName('');
        setPresent(false);
    };

    return (
        <div>
            <Typography variant="h4" gutterBottom>Attendance</Typography>
            <form onSubmit={handleSubmit}>
                <TextField
                    label="Student Name"
                    variant="outlined"
                    value={studentName}
                    onChange={(e) => setStudentName(e.target.value)}
                    required
                    fullWidth
                    margin="normal"
                />
                <FormControlLabel
                    control={
                        <Checkbox
                            checked={present}
                            onChange={(e) => setPresent(e.target.checked)}
                        />
                    }
                    label="Present"
                />
                <Button type="submit" variant="contained" color="primary">Submit</Button>
            </form>
            <Typography variant="h5" gutterBottom>Attendance Records</Typography>
            <List>
                {attendanceRecords.map((record) => (
                    <ListItem key={record._id}>
                        {record.studentName} - {record.present ? 'Present' : 'Absent'} on {new Date(record.date).toLocaleDateString()}
                    </ListItem>
                ))}
            </List>
        </div>
    );
};

export default AttendanceForm;
